var lastScrollHeight = 0; // This will hold the last scroll height
function scrollToEnd() {
    var d = document.documentElement;
    var scrollHeight = Math.max(d.scrollHeight, d.offsetHeight, d.clientHeight);
    window.scrollTo(0, scrollHeight);

    // If the scroll position does not change, end the loop
